mapper.select:
SELECT COUNT(DISTINCT address)
FROM peer_records
WHERE downloader = #{downloader}
    AND lastTimeSeen <= #{startAt}
    AND firstTimeSeen >= #{endAt}

SELECT * FROM peer_records
WHERE address = #{address}
ORDER BY ${orderBySql}

SELECT peerId, clientName, SUM(uploaded) AS uploaded, SUM(downloaded) AS downloaded, COUNT(*) AS ct
FROM peer_records
WHERE firstTimeSeen >= #{startAt} AND lastTimeSeen <= #{endAt} AND uploaded > 0 AND downloaded > 0

    AND downloader = #{downloader}

GROUP BY peerId, clientName
ORDER BY ${orderBySql}

SELECT torrent_id AS torrentId, COUNT(*) AS count FROM peer_records WHERE torrent_id IN
#{id}
GROUP BY torrent_id
