mapper.select:
SELECT * FROM peer_records
WHERE address = #{ip}
ORDER BY ${orderBySql}

SELECT peer_id, client_name, SUM(uploaded) AS uploaded, SUM(downloaded) AS downloaded, COUNT(*) AS ct
FROM peer_records
WHERE first_time_seen >= #{startAt} AND last_time_seen <= #{endAt} AND uploaded > 0 AND downloaded > 0

    AND downloader = #{downloader}

GROUP BY peer_id, client_name
ORDER BY ${orderBySql}

SELECT torrent_id AS torrentId, COUNT(*) AS count FROM peer_records WHERE torrent_id IN
#{id}
GROUP BY torrent_id

SELECT DISTINCT address
FROM peer_records
WHERE last_time_seen BETWEEN #{start} AND #{end}

    AND downloader = #{downloader}
